import React, { Component } from "react";

//Appクラス = Appコンポーネント(カスタムタグ)
class HoverButton extends Component {
  render() {
    const slideBlogBtn = {
      backgroundColor: "#78B9FA",
      color: "#FFFFFF",
      width: "22.6vw",
      height: "5.36vw",
      fontSize: "1.90vw",
      padding: "1.02vw 2.66vw",
      borderRadius: "4.0vw",
      textDecoration: "none",
    };

    return (
      <a
        href="https://blog.opuappnavi.com/"
        target="blank"
        style={slideBlogBtn}
        onMouseEnter={() =>
          this.setState({ imageUrl: "/images/button_shadow.png" })
        }
        onMouseLeave={() =>
          this.setState({ imageUrl: "/images/button_noshadow.png" })
        }
      >
        アプリNaviブログ
      </a>
    );
  }
}

//他の場所で読み込んで使えるようにexport
export default HoverButton;
