import React  from 'react';
import GoogleMapReact from 'google-map-react';
//import appnaviMarker from '../images/appnaviMarker.png';
import './marker.css';

const Marker = (props) => ({
  render(){
    return (
      <div>
      <div className="pin"></div>
      <div className="pulse"></div>
      </div>
      
    )
    
  }
    
})

const SimpleMap = (props) => ({
  render(){

    return (
      <div style={{'width':800,'height':800}}>
      <GoogleMapReact
        bootstrapURLKeys={'AIzaSyASIEu5tFEl66Dcm7JR6NDp3jSUe8Mzuxs' }
        defaultCenter={{lat: props.lat,long: props.lng}}
        defaultZoom={15}>
        <Marker lat={props.lat} lng={props.lng} />
      </GoogleMapReact>
      </div>
    )
  
  }
  })

export { SimpleMap, Marker };


